import React from 'react';

type LibroRowProps = {
  id: number;
  titulo: string;
  imagen?: string;
  precio: number;
  disponible: boolean;
  editorial?: { nombre: string };
  genero?: { nombre: string };
};

export default function LibroRow({
  id,
  titulo,
  imagen,
  precio,
  disponible,
  editorial,
  genero,
}: LibroRowProps) {
  return (
    <tr>
      <td>{id}</td>
      <td>{titulo}</td>
      <td>
        {imagen ? (
          <img
            src={`http://localhost:3000/uploads/${imagen}`}
            alt={titulo}
            style={{
              width: '50px',
              height: '75px',
              objectFit: 'cover',
              borderRadius: '4px',
              boxShadow: '0 0 3px rgba(0,0,0,0.2)',
            }}
          />
        ) : (
          <span className="text-muted">Sin imagen</span>
        )}
      </td>
      <td>{precio}</td>
      <td>{editorial?.nombre || '—'}</td>
      <td>{genero?.nombre || '—'}</td>
      <td>{disponible ? 'Sí' : 'No'}</td>
    </tr>
  );
}
