import React, { useEffect, useState } from 'react';
import axios from '../config/axios';

type Libro = {
  id: number;
  titulo: string;
  precio: number;
  disponible: boolean;
  autor_id: number;
  editorial_id: number;
  genero_id: number;
};

export default function LibrosListPage() {
  const [libros, setLibros] = useState<Libro[]>([]);
  const [loading, setLoading] = useState(true);

  // Estados para paginación
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const limit = 10; // items por página

  // Función para cargar libros según página actual
  const loadLibros = (pageNum: number) => {
    setLoading(true);
    axios.get('/libros', {
      params: { page: pageNum, limit },
    })
    .then(res => {
      setLibros(res.data.libros || res.data); // si el backend envía libros en res.data.libros
      setTotalPages(res.data.totalPages || 1); // ajusta si backend envía totalPages
      setPage(pageNum);
    })
    .catch(() => {
      alert('Error al cargar los libros');
    })
    .finally(() => setLoading(false));
  };

  useEffect(() => {
    loadLibros(page);
  }, []);

  if (loading) return <p>Cargando libros...</p>;

  return (
    <div className="container py-4">
      <h1>Listado de Libros</h1>
      <table className="table table-bordered">
        <thead>
          <tr>
            <th>ID</th>
            <th>Título</th>
            <th>Precio</th>
            <th>Disponible</th>
          </tr>
        </thead>
        <tbody>
          {libros.map(libro => (
            <tr key={libro.id}>
              <td>{libro.id}</td>
              <td>{libro.titulo}</td>
              <td>{libro.precio}</td>
              <td>{libro.disponible ? 'Sí' : 'No'}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Paginación */}
      <nav>
        <ul className="pagination justify-content-center">
          <li className={`page-item ${page === 1 ? 'disabled' : ''}`}>
            <button
              className="page-link"
              onClick={() => loadLibros(page - 1)}
              disabled={page === 1}
            >
              Anterior
            </button>
          </li>

          {[...Array(totalPages)].map((_, i) => (
            <li key={i} className={`page-item ${page === i + 1 ? 'active' : ''}`}>
              <button
                className="page-link"
                onClick={() => loadLibros(i + 1)}
              >
                {i + 1}
              </button>
            </li>
          ))}

          <li className={`page-item ${page === totalPages ? 'disabled' : ''}`}>
            <button
              className="page-link"
              onClick={() => loadLibros(page + 1)}
              disabled={page === totalPages}
            >
              Siguiente
            </button>
          </li>
        </ul>
      </nav>
    </div>
  );
}
