import React, { useEffect, useState } from 'react';
import axios from '../config/axios';

type Libro = {
  id: number;
  titulo: string;
  precio: number;
  disponible: boolean;
  autor_id: number;
  editorial_id: number;
  genero_id: number;
};

export default function LibrosListPage() {
  const [libros, setLibros] = useState<Libro[]>([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const fetchLibros = (pageNumber: number) => {
    setLoading(true);
    axios.get('/libros', { params: { page: pageNumber, limit: 10 } })
      .then(res => {
        setLibros(res.data.libros);
        setPage(res.data.page);
        setTotalPages(res.data.totalPages);
      })
      .catch(() => alert('Error al cargar los libros'))
      .finally(() => setLoading(false));
  };

  useEffect(() => {
    fetchLibros(page);
  }, [page]);

  const handlePageChange = (newPage: number) => {
    if(newPage >= 1 && newPage <= totalPages){
      setPage(newPage);
    }
  };

  if (loading) return <p>Cargando libros...</p>;

  return (
    <div className="container mt-4">
      <h1>Listado de Libros</h1>
      <table className="table table-bordered">
        <thead>
          <tr>
            <th>ID</th>
            <th>Título</th>
            <th>Precio</th>
            <th>Disponible</th>
          </tr>
        </thead>
        <tbody>
          {libros.map(libro => (
            <tr key={libro.id}>
              <td>{libro.id}</td>
              <td>{libro.titulo}</td>
              <td>{libro.precio}</td>
              <td>{libro.disponible ? 'Sí' : 'No'}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <nav>
        <ul className="pagination justify-content-center">
          <li className={`page-item ${page === 1 ? 'disabled' : ''}`}>
            <button className="page-link" onClick={() => handlePageChange(page - 1)}>Anterior</button>
          </li>

          {/* Mostrar botones para las páginas */}
          {[...Array(totalPages)].map((_, i) => {
            const pageNum = i + 1;
            return (
              <li
                key={pageNum}
                className={`page-item ${page === pageNum ? 'active' : ''}`}
              >
                <button className="page-link" onClick={() => handlePageChange(pageNum)}>
                  {pageNum}
                </button>
              </li>
            );
          })}

          <li className={`page-item ${page === totalPages ? 'disabled' : ''}`}>
            <button className="page-link" onClick={() => handlePageChange(page + 1)}>Siguiente</button>
          </li>
        </ul>
      </nav>
    </div>
  );
}
