import React, { useEffect, useState } from 'react';
import axios from '../config/axios'; // así como ya lo tienes

type Libro = {
  id: number;
  titulo: string;
  precio: number;
  disponible: boolean;
  autor_id: number;
  editorial_id: number;
  genero_id: number;
  // agrega más campos si quieres mostrar
};

export default function LibrosListPage() {
  const [libros, setLibros] = useState<Libro[]>([]);
  const [loading, setLoading] = useState(true);

  console.log('ffffffff', libros)

  useEffect(() => {
    axios.get('/libros')
      .then(res => {
        setLibros(res.data);
      })
      .catch(err => {
        alert('Error al cargar los libros');
      })
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <p>Cargando libros...</p>;

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Listado de Libros</h1>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th style={{ border: '1px solid #ccc', padding: 8 }}>ID</th>
            <th style={{ border: '1px solid #ccc', padding: 8 }}>Título</th>
            <th style={{ border: '1px solid #ccc', padding: 8 }}>Precio</th>
            <th style={{ border: '1px solid #ccc', padding: 8 }}>Disponible</th>
          </tr>
        </thead>
        <tbody>
          {libros.map(libro => (
            <tr key={libro.id}>
              <td style={{ border: '1px solid #ccc', padding: 8 }}>{libro.id}</td>
              <td style={{ border: '1px solid #ccc', padding: 8 }}>{libro.titulo}</td>
              <td style={{ border: '1px solid #ccc', padding: 8 }}>{libro.precio}</td>
              <td style={{ border: '1px solid #ccc', padding: 8 }}>{libro.disponible ? 'Sí' : 'No'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
