import React from 'react';
import { Libro } from '../types';

type LibroRowProps = {
  libro: Libro;  // Aquí definimos que recibe la prop 'libro'
};

export default function LibroRow({ libro }: LibroRowProps) {
  return (
    <tr>
      <td>{libro.id}</td>
      <td>{libro.titulo}</td>
      <td>
        {libro.imagen ? (
          <img
            src={`http://localhost:3000/uploads/${libro.imagen}`}
            alt={libro.titulo}
            style={{ width: 50, height: 75, objectFit: 'cover', borderRadius: 4 }}
          />
        ) : (
          <span className="text-muted">Sin imagen</span>
        )}
      </td>
      <td>{libro.precio}</td>
      <td>{libro.editorial?.nombre || '—'}</td>
      <td>{libro.genero?.nombre || '—'}</td>
      <td>{libro.disponible ? 'Sí' : 'No'}</td>
    </tr>
  );
}
